<script lang="ts">
import { reactive, toRefs, watch } from 'vue';
import isEqual from 'lodash-es/isEqual';
const customEventList = [
  'update:fontWeight',
  'update:fontStyle',
  'update:color',
  'update:fontSize',
  'update:lineHeight',
  'update:textAlign',
  'update:textDecoration',
];
export default {
  props: {
    fontWeight: {
      type: String,
      default: '',
    },
    fontStyle: {
      type: String,
      default: '',
    },
    color: {
      type: String,
      default: '',
    },
    fontSize: {
      type: Object,
      default() {return {}}
    },
    lineHeight: {
      type: Object,
      default() {return {}}
    },
    textAlign: {
      type: String,
      default: '',
    },
    textDecoration: {
      type: String,
      default: '',
    },
    componentId: {
      type: String,
      default: '',
    }
  },
  emits: customEventList,
  setup (props, {emit}) {
    const state = reactive({
      childOpen: false,
      fontQuick: '',
      labelCol: {
        style: {
          'display': 'flex',
          'align-items': 'center'
        }
      },
      labelWrapCol: {
        style: {
          'display': 'flex',
          'align-items': 'center',
          'width': '70px'
        }
      },
      wrapperCol: {
        flex: '1',
      },
      quickList: [
        {
          label: '一级标题',
          value: {
            fontWeight: '700',
            fontSize: {
              value: '20',
              unit: 'px'
            },
            lineHeight: {
              value: '30',
              unit: 'px'
            }
          },
          id: '1'
        },
        {
          label: '二级标题',
          value: {
            fontWeight: '700',
            fontSize: {
              value: '16',
              unit: 'px'
            },
            lineHeight: {
              value: '24',
              unit: 'px'
            }
          },
          id: '2'
        },
        {
          label: '三级标题',
          value: {
            fontWeight: '700',
            fontSize: {
              value: '14',
              unit: 'px'
            },
            lineHeight: {
              value: '22',
              unit: 'px'
            }
          },
          id: '3'
        },
        {
          label: '段落',
          value: {
            fontWeight: '400',
            fontSize: {
              value: '14',
              unit: 'px'
            },
            lineHeight: {
              value: '22',
              unit: 'px'
            }
          },
          id: '4'
        },
        {
          label: '默认',
          value: {
            fontWeight: '',
            fontSize: {
              value: '',
              unit: 'px'
            },
            lineHeight: {
              value: '',
              unit: 'px'
            }
          },
          id: ''
        },
        {
          label: '自定义',
          value: {},
          id: 'custom'
        }
      ],
      fontWeightList: [
        {
          label: '100 Thine',
          value: 100
        },
        {
          label: '100 Extra Light',
          value: 200
        },
        {
          label: '100 Light',
          value: 300
        },
        {
          label: '400 Normal',
          value: 400
        },
        {
          label: '500 Medium',
          value: 500
        },
        {
          label: '600 Semi Bold',
          value: 600
        },
        {
          label: '700 Bold',
          value: 700
        },
        {
          label: '800 Extra Bold',
          value: 800
        },
        {
          label: '900 Black',
          value: 900
        },
        {
          label: 'Lighter',
          value: 'lighter'
        },
        {
          label: 'Bolder',
          value: 'bolder'
        }
      ]
    })

    const setInitFontQuick = () => {
      const findIndex = state.quickList.findIndex(item => {
        return isEqual(item.value, {
          fontWeight: props.fontWeight,
          fontSize: props.fontSize,
          lineHeight: props.lineHeight,
        })
      });

      if (findIndex >= 0) {
        state.fontQuick = state.quickList[findIndex].id;
      }
    }
    watch(() => props.componentId, () => {
      setInitFontQuick();
    },
    {
      immediate: true,
      deep: true,
    })
   
    const handlChildOpen = () => {
      state.childOpen = !state.childOpen;
    };

    const handleFontQuick = () => {
      const quickItem = state.quickList.find(item => item.id === state.fontQuick);
      if(state.fontQuick === 'custom') {
        state.childOpen = true;
      }
      if (quickItem) {
        Object.keys(quickItem.value).forEach(key => {
          emit(`update:${key}` as any, quickItem.value[key]);
        })
      }
    }

    const handleChange = (type, data) => {
      state.fontQuick = 'custom';
      emit(`update:${type}` as any, data.target ? data.target.value : data);
    }

    const handleObjectChange = (key1, key2, data) => {
      const currentProp = props[key1];
      state.fontQuick = 'custom';
      let temp = {};
      if (key2 === 'value') {
        temp = {
          value: data,
          unit: currentProp['unit'],
        }
      } else {
        temp = {
          value: currentProp.value,
          unit: data,
        }
      }
      state.fontQuick = 'custom';
      emit(`update:${key1}` as any, temp);
    }

    return {
      ...toRefs(state),
      handlChildOpen,
      handleFontQuick,
      handleChange,
      handleObjectChange,
    }
  }
}
</script>
